CREATE TABLE civdocs.sections (
    `author` VARCHAR(36) CHARACTER SET utf8,
    `date` VARCHAR(26) CHARACTER SET utf8,
    `document_id` INT,
    `section_id` INT,
    `next_section_id` INT,
    `prev_section_id` INT,
    `section_index` INT,
    `next_section_index` INT,
    `prev_section_index` INT,
    `paragraphs_total` INT,
    `subtitle` VARCHAR(128) CHARACTER SET utf8,
    `title` VARCHAR(17) CHARACTER SET utf8,
    `sections_total` INT,
    PRIMARY KEY(section_id)
);

